(305, 'sccss_settings', 'a:1:{s:13:\"sccss-content\";s:9280:\"/* Enter Your Custom CSS Here */\r\n\r\n\r\n\r\n/*******PAGE OUR TEAM**********/ \r\n/*OUR TEAM style*/\r\n\r\n.team{\r\ntext-align:center;\r\nwidth:100%;\r\n/*border:2px solid gray;\r\nbackground:silver;\r\nbox-shadow:0 0 10pt #727272;\r\n//padding:10pt;\r\nborder-radius:4pt;*/ }\r\n\r\n.team-item{\r\nmargin:10pt;\r\npadding:5pt;\r\nborder:2px solid silver;\r\nborder-radius:4pt;\r\nwidth:150pt;\r\nheight:auto;\r\ndisplay:inline-block;\r\nvertical-align: center; vertical-align:top;\r\ntext-align:center;   box-shadow:0 0 5pt #727272;\r\n}\r\n\r\n.team-item img{\r\n  width:150px; height:150px;  \r\nborder:1px solid #ddd;    \r\nborder-radius: 50%;\r\nbox-shadow: 0 2px 5px #aaa;\r\nmargin: 5% auto;\r\n/*position: absolute;*/\r\ntop: 0; left: 0; bottom: 0; right: 0;\r\ntransition: all 0.3s ease-in-out;\r\n-moz-transition: all 0.3s ease-in-out;\r\n-webkit-transition: all 0.3s ease-in-out;\r\n-o-transition: all 0.3s ease-in-out;    */\r\n }\r\n.team-item img:hover {\r\nborder-radius: 3px;\r\n }\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n/***********HEADER**************/\r\n\r\n/*****Header widget in the right corner with CONTACT details****/\r\n.header-widget{\r\nbackground: #e62e00;//#b30000\r\nwidth:220px; height:120px;\r\npadding-left:12px;\r\nborder-radius: 0px 0px 125px 125px;\r\nvertical-align:middle;\r\nbox-shadow: 0px 0px 35px 0px #f8da07;\r\ntext-align: center;\r\ncolor:#ffffff;\r\nfont-weight:600;\r\nfloat:right;\r\n}\r\n\r\n.header-image{\r\nmargin-top:-80px;} //putting header up\r\n\r\n\r\n\r\n\r\n/*****MENU******/\r\n.main-nav {width:85%;\r\nmargin-top:-60px; //putting menu up to the header\r\n/*border-radius: 0px 0px 120px 120px;*/}  \r\n\r\n#menu-primary-menu{\r\nposition:relative;\r\nmargin-top:-50px;\r\nleft:0px;\r\nwidth:85%; \r\nfloat:right;\r\n}\r\n\r\n\r\n\r\n\r\n\r\n/******CALL SUPPORT DONATION IN GP HOOKS ******/\r\n.support{\r\nwidth:855px; height:45px;\r\n}\r\n\r\n.sppt_txt{\r\ndisplay:inline-block;\r\n/*text-align: left; float:left;*/\r\nwidth:800px; }\r\n\r\n.sppt_pp{\r\nwidth:130px;\r\ndisplay:inline-block;\r\nmargin:0 auto;\r\n/*text-align: left; */float:left;}\r\n\r\n.sppt_pp form tbody td{\r\n	top:-45px;\r\n	left:800px;\r\n	position:relative;\r\n}\r\n#donation_buttons input{\r\n  width:142px;\r\n  height:27px;}\r\n\r\n.sppt_txt :hover{\r\n  color:red;\r\n  }\r\n\r\n/*.page-id-616 .support {display: none;  //hide donation button on Home page\r\n}*/\r\n/*END DONATION*/\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n/**************RIGHT SIDEBAR SOCIAL BUTTONS***************/\r\n\r\n/*\r\n#donation_buttons .page-sidebar.widget form table td {\r\nwidth:173px!important;\r\nheight:53px!important;\r\n/*float:left;*/\r\n/*margin-right:120px;\r\nmargin-top:-80px;*/}*/\r\n\r\n\r\n/*.follow_right{\r\nwidth:180px;\r\nmargin:0 auto;\r\npadding-left:5px;\r\n\r\n}\r\n\r\n\r\n\r\n.subscribe{\r\nwidth:150px; //173;\r\nheight:35px;  //40;\r\nborder-radius:8px;\r\nmargin-top:0;}*/\r\n\r\n\r\n/*FOLLOW US AND SUBSCRIBE BUTTONS */\r\n.textwidget{width:100%;}\r\n\r\n.follow_us{ \r\nwidth:180px!important;\r\nmargin:0 auto;\r\n}\r\n\r\n.subscribe {\r\nwidth:170px; \r\nheight:40px;\r\nmargin:0 auto;\r\nborder-radius:8px;\r\n}\r\n/*right sidebar*/\r\n#text-20{\r\npadding-top:20px;\r\npadding-bottom:15px!important;  \r\n}\r\n/*END FOLLOW US*/\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n/*SUBSCRIBE CONTACT POPUP FORM for workshop*/\r\n #fancybox-wrap{\r\nbackground:#1e72bd!important;\r\n-moz-box-shadow: 0 0 6px #000;\r\n-webkit-box-shadow: 0 0 6px #000;\r\nbox-shadow:0 0 6px #000;  \r\nwidth:300px!important;\r\n}\r\n\r\n.wpcf7-form .wrapper{\r\n	width:300px;\r\n}\r\n.wrapper .buttn{\r\n	width:200px;\r\n}\r\n\r\n#fancybox-outer{\r\nbackground:none!important;\r\n}\r\n\r\n#fancybox-outer>div, #fancybox-content,  #contact_form_pop_up{\r\nbackground:none!important;\r\nborder:none!important;\r\n}\r\n\r\n#contact_form_pop_up{\r\npadding: 10px 0px 0px 20px!important;\r\n}\r\n\r\n#contact_form_pop {\r\nwidth:300px!important;\r\n}\r\n#contact_form_pop p {\r\ncolor:#fff!important;\r\nfont-size: 14px;\r\n \r\n}\r\n\r\n.wpcf7 {\r\ncolor:#fff!important;\r\nbackground:#1e72bd!important; /*15567E*/\r\nwidth:280px; /*width for form*/\r\npadding:0px!important;/*zero padding for pop up form, for form on the page then setup padding 20px*/\r\noverflow:hidden;\r\n}\r\n\r\n#contact_form_pop h4{\r\ncolor:#fff!important;/*\r\nalign:center!important;*/\r\n\r\n}\r\n\r\n.wpcf7 input[type=\"text\"], .wpcf7 input[type=\"email\"], .wpcf7 textarea{\r\nborder:1px solid red;\r\npadding:4px!important;\r\nborder-radius:5px;\r\nborder:1px solid #f1f1f1;\r\n}\r\n\r\n.wpcf7 input[type=\"text\"]:focus, .wpcf7 input[type=\"email\"]:focus, .wpcf7 textarea:focus{\r\nborder:1px solid #fff;\r\n-moz-box-shadow: 0 0 6px #fff;\r\n-webkit-box-shadow: 0 0 6px #fff;\r\nbox-shadow:0 0 6px #fff;   \r\n}\r\n\r\n.wpcf7 input[type=\"text\"], .wpcf7 input[type=\"email\"], .wpcf7 textarea{\r\nwidth:270px!important;\r\n}\r\n\r\n.wpcf7-submit{\r\nbackground: linear-gradient(#f1f1f1, #8b8b8b), #f1f1f1!important;\r\ncolor:#000!important;\r\ntext-transform:uppercase;\r\nfloat:right;\r\nmargin:20px!important;\r\n}\r\n\r\n.wpcf7-submit:hover{\r\n-moz-box-shadow: 0 0 6px #000;\r\n-webkit-box-shadow: 0 0 6px #000;\r\nbox-shadow:0 0 6px #000;  \r\nbackground-image: linear-gradient( #8b8b8b, #f1f1f1), #8b8b8b!important;\r\n}\r\n\r\n\r\n/*CONTACT FORM ON THE PAGE CONTACT*/\r\n#wpcf7-f173-p546-o1.wpcf7 {\r\nwidth: 300px;\r\nbackground: #1e72bd!important;\r\nborder: 2px solid #0088cc!important;\r\n}\r\n\r\n.p_cform.alignleft {\r\nmargin-right:100px;\r\n}\r\n.p_cform.alignleft p{\r\ncolor:#000000;\r\nfont-size:0.8em;\r\n}\r\n\r\n.your-name input[type=text], .your-subject input[type=text], .your-message .wpcf7-textarea{\r\n	border-color:2px solid #125e87;\r\n}\r\n\r\n.wpcf7-email.your-email input[type=email]{\r\n	border-color:1px solid #125e87;\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n/************ABOUT ALA PAGE************/\r\n/********short page content **********/\r\n.p_content{\r\nwidth:550px;//280px;\r\nborder: 2px solid #125e87;// #0080ff\r\nbackground: #e6f2ff;\r\npadding:15px;\r\nborder-radius:5px;\r\n\r\n}\r\n\r\n\r\n\r\n/*************THEATRE FOR CHANGE PAGE***********/\r\n/*********Resize Photo THUMBNAILS in Performance Theatre for Change*********/\r\n.thubm{\r\nwidth:550px;\r\ndisplay:inline-block;}\r\n\r\nimg.thumbnail.alignleft{\r\nfloat:left;\r\nmargin:5px 15px 5px 0;\r\n}\r\nimg.thumbnail.alignright{\r\nfloat:right;\r\nmargin:5px 0 5px 15px;\r\n}\r\nimg.thumbnail.aligncenter{\r\nmargin:5px auto;\r\ndisplay: block;\r\n}\r\nimg.thumbnail {\r\nmax-height: 150px;\r\nmax-width: 215px;\r\ntransition: all 0.3s linear 0s;\r\nz-index: 1;\r\nborder: 1px solid #EEE;\r\nbackground: #FFFFFF;\r\npadding: 5px;\r\n}\r\nimg.thumbnail:hover {\r\ncursor:pointer;\r\nbox-shadow:0 0 5pt #727272;/*box-shadow: 0 0 5px 5px #eee;*/\r\nborder: 1px solid #125e87;//#25A0E3;\r\nz-index: 10;\r\ntransition: all 0.3s linear 0.3s;\r\n}\r\nimg.thumbnail.alignleft:hover{\r\ntransform:scale(2) translate(88px, 30px);\r\n}\r\nimg.thumbnail.alignright:hover{\r\ntransform:scale(2) translate(-88px, 30px);\r\n}\r\nimg.thumbnail.aligncenter:hover {\r\ntransform:scale(2);\r\n}\r\n/*THUMBNAILS*/\r\n\r\n\r\n/************FOOTER*************/\r\n/*****WIDGET*********/\r\n.cofounder{\r\nwidth:1000px;\r\nheight:60px;\r\nmargin: 0 auto;\r\n\r\n}\r\n\r\n.cofounder img{\r\npadding:3px;\r\n	}\r\n\r\n\r\n\r\n\r\n/*************HOME PAGE****************/\r\n/*CENTRAL BLOCK*/\r\n\r\n.wrapper{\r\nmargin: 0 auto;\r\nwidth:100%;\r\nborder-radius: 8px; \r\n/*max-width:1120px;/*Максимальная ширина при больших разрешениях*/\r\n  /*  min-width:940px;/*Минимальная ширина при маленьких разрешениях*/\r\n   /* width:auto;/*Ширина в зависимости от разрешения экрана*/\r\n}\r\n\r\n.item {\r\n display: inline-block;\r\n background: #fff;\r\n margin: 0 auto;\r\n width: 100%;\r\n  overflow:auto; \r\n border-radius: 8px; \r\n box-shadow: 2px 4px 8px 1px #ccc; /* outer shadow */\r\n}\r\n\r\n\r\n .item p{\r\npadding-right:50px;}\r\n\r\n.item img{\r\npadding:30px; \r\nborder-radius: 10px;\r\n }\r\n\r\n\r\n \r\n/* links styles inside block */\r\n.item a {\r\ntext-decoration: none;\r\ncolor: #359CC6;\r\nmargin: 0 10px;\r\n}\r\n/* links hover */\r\n.item a:hover {\r\ncolor: #E88F00;    \r\nborder-bottom: 1px dotted #9F1D35;\r\n}\r\n\r\n\r\n/*SMALL TILES ON HOME PAGE*/\r\n.feature {\r\n	float:left;\r\n	width:33.3%;\r\n  	margin: 20px auto;\r\n}\r\n\r\n.feature:hover {\r\n	opacity: 0.8;\r\n}\r\n.feature-card {\r\n	background-color: #fff;\r\n	border-radius: 8px;\r\n    box-shadow: 2px 4px 8px 1px #ccc; /* outer shadow */\r\n	margin: 0px 10px 20px 10px;\r\n	min-height: 440px;\r\n }\r\n\r\n.feature h1, h2,  p {\r\n    margin-left: 20px;\r\n    margin-right: 20px;\r\n}\r\np {\r\n    margin-bottom: 1.5em;\r\n}\r\n\r\n.feature img {\r\n	width: 100%;\r\n	height: auto;\r\n    margin: 20px auto;   \r\n	border-radius: 8px 8px 0 0;\r\n}\r\n\r\n.feature h2, .item h2{\r\n    color: #222;\r\n    font-size: 18px;\r\n    font-weight: 400;\r\n    letter-spacing: -.02em;\r\n    line-height: 30px;\r\n    padding: 5px 0 10px;\r\n    text-transform: uppercase;\r\n}\r\n.feature h1, .feature h2, .feature p {\r\n    margin-left: 20px;\r\n    margin-right: 20px;\r\n}\r\n\r\n/* links styles inside block */\r\n.feature a {\r\ntext-decoration: none;\r\ncolor: #359CC6;\r\nmargin: 0 10px;\r\n}\r\n/* links hover */\r\n.feature a:hover {\r\ncolor: #E88F00;    \r\nborder-bottom: 1px dotted #9F1D35;\r\n}\r\n\r\n\r\n/**************COMMUNITY WORKSHOP PAGE*********/\r\n/*Facilitators*/\r\n.facilitotors_col {\r\n   // background:#C5DFF0;\r\n    color:#000;\r\n    margin:20px auto;\r\n    padding:10px;\r\n    position:relative;\r\n    width:800px;\r\n \r\n    border-radius:5px;\r\n    -moz-border-radius:5px;\r\n    -webkit-border-radius:5px;\r\n \r\n   /* box-shadow:1px 1px 5px #111111;*/\r\n \r\n    column-count: 3;\r\n    column